mapper.select:
SELECT article_id, article_name, article_image, article_type_id, article_owner_id, article_desc, article_text, article_create_time, article_last_edit_time, article_like, article_priority FROM tb_article
AND article_type = #{articleType.articleTypeId}
AND article_owner_id = #{owner.userId}
AND article_id = #{article.articleId}
ORDER BY article_id ASC LIMIT #{rowIndex},#{pageSize}

mapper.update:
UPDATE tb_article
SET article_image = #{articleImage}
WHERE article_id = #{articleId}

mapper.select:
SELECT article_id, article_name, article_image, article_type_id, article_owner_id, article_desc, article_create_time, article_priority FROM tb_article
-- 根据类型查找文章 AND article_type = #{articleType.articleTypeId}
-- 根据作者查找文章 AND article_owner_id = #{article.articleOwner.userId}
AND article_id = #{article.articleId}
ORDER BY article_id ASC LIMIT #{rowIndex},#{pageSize}

SELECT article_id,
article_name,
article_type_id,
article_owner_id,
article_last_edit_time
FROM tb_article

    article_owner_id = #{article.articleOwner.userId}
    
        AND article_type_id = #{article.articleType.articleTypeId}
    

 ORDER BY article_id ASC
LIMIT #{rowIndex},#{pageSize}

